playBells();

playSojiro();

function playBells() {
	var audioBells = new Audio('https://aidenpearce9.github.io/Rock-Paper-Scissors/wav/bells.wav');
	audioBells.play();
}

function playSojiro() {
	setTimeout(function() {var audioYoureBack = new Audio('https://aidenpearce9.github.io/Rock-Paper-Scissors/wav/youre-back.wav');
	audioYoureBack.play();}, 1000);
}

let userChoice;

document.querySelector("#rock").addEventListener('click', function() {
  userChoice = 1;
  result();
})

document.querySelector("#paper").addEventListener('click', function() {
  userChoice = 2;
  result();
})

document.querySelector("#scissors").addEventListener('click', function() {
  userChoice = 3;
  result();
})

let randomNumber;

function result() {
  let randomNumber = Math.ceil(Math.random() * 3);
  if (userChoice == 1) {
    document.querySelector('#rock-pic').style.visibility = 'visible';
    document.querySelector('#paper-pic').style.visibility = 'hidden';
    document.querySelector('#scissors-pic').style.visibility = 'hidden';
    document.querySelector('#rock').style.color = '#f00';
    document.querySelector('#paper').style.color = '#fff';
    document.querySelector('#scissors').style.color = '#fff';
    document.querySelector('#rock').style.fontSize = '28px';
    document.querySelector('#paper').style.fontSize = '25px';
    document.querySelector('#scissors').style.fontSize = '20';
    document.querySelector('#rock').style.transform = 'rotate(18deg)';
    document.querySelector('#paper').style.transform = 'rotate(5deg)';
    document.querySelector('#scissors').style.transform = 'rotate(-15deg)';
    document.querySelector('#rock').style.margin = '33% 33% 0 0';
    document.querySelector('#paper').style.margin = '3.5% 40% 0 0';
    document.querySelector('#scissors').style.margin = '7.5%  40% 0 0';
    switch(randomNumber) {
      case 1:
        document.querySelector('#who-won').innerHTML = `Opponent chose Rock. <br>It's a -<span class="blue">DRAW</span>-`;
        var audioDraw = new Audio('mp3/draw.wav');
	      audioDraw.play();
        break;
      case 2:
        document.querySelector('#who-won').innerHTML = `Opponent chose Paper. <br>You <span class="red">LOSE</span>..`;
        var audioLose = new Audio('mp3/lose.wav');
	      audioLose.play();
        break;
      case 3:
        document.querySelector('#who-won').innerHTML = `Opponent chose Scissors. <br>You <span class="green">WIN</span>!!`;
        var critSound = new Audio('https://fleury14.github.io/cyo/sound/persona5-crit.wav');
        critSound.play();
        break;
    }
  } else if (userChoice == 2) {
    document.querySelector('#rock-pic').style.visibility = 'hidden';
    document.querySelector('#paper-pic').style.visibility = 'visible';
    document.querySelector('#scissors-pic').style.visibility = 'hidden';
    document.querySelector('#rock').style.color = '#fff';
    document.querySelector('#paper').style.color = '#f00';
    document.querySelector('#scissors').style.color = '#fff';
    document.querySelector('#rock').style.transform = 'rotate(11deg)';
    document.querySelector('#paper').style.transform = 'rotate(1deg)';
    document.querySelector('#scissors').style.transform = 'rotate(-15deg)';
    document.querySelector('#rock').style.fontSize = '25px';
    document.querySelector('#paper').style.fontSize = '25px';
    document.querySelector('#scissors').style.fontSize = '20px';
    document.querySelector('#rock').style.margin = '33% 38% 0 0';
    document.querySelector('#paper').style.margin = '4% 45% 0 0';
    document.querySelector('#scissors').style.margin = '8.5%  45% 0 0';
    switch(randomNumber) {
      case 1:
        document.querySelector('#who-won').innerHTML = `Opponent chose Rock. <br>You <span class="green">WIN</span>!!`;
        var critSound = new Audio('https://fleury14.github.io/cyo/sound/persona5-crit.wav');
        critSound.play();
        break;
      case 2:
        document.querySelector('#who-won').innerHTML = `Opponent chose Paper. <br>It's a -<span class="blue">DRAW</span>-`;
        var audioDraw = new Audio('mp3/draw.wav');
	      audioDraw.play();
        break;
      case 3:
        document.querySelector('#who-won').innerHTML = `Opponent chose Scissors. <br>You <span class="red">LOSE</span>..`;
        var audioLose = new Audio('mp3/lose.wav');
	      audioLose.play();
        break;
    }
  } else {
    document.querySelector('#rock-pic').style.visibility = 'hidden';
    document.querySelector('#paper-pic').style.visibility = 'hidden';
    document.querySelector('#scissors-pic').style.visibility = 'visible';
    document.querySelector('#rock').style.color = '#fff';
    document.querySelector('#paper').style.color = '#fff';
    document.querySelector('#scissors').style.color = '#f00';
    document.querySelector('#rock').style.transform = 'rotate(6deg)';
    document.querySelector('#paper').style.transform = 'rotate(-3deg)';
    document.querySelector('#scissors').style.transform = 'rotate(-8deg)';
    document.querySelector('#rock').style.fontSize = '30px';
    document.querySelector('#paper').style.fontSize = '23px';
    document.querySelector('#scissors').style.fontSize = '27px';
    document.querySelector('#rock').style.margin = '33% 47% 0 0';
    document.querySelector('#paper').style.margin = '5.5% 38% 0 0';
    document.querySelector('#scissors').style.margin = '5.5%  45% 0 0';
    switch(randomNumber) {
      case 1:
        document.querySelector('#who-won').innerHTML = `Opponent chose Rock. <br>You <span class="red">LOSE</span>..`;
        var audioLose = new Audio('mp3/lose.wav');
	      audioLose.play();
        break;
      case 2:
        document.querySelector('#who-won').innerHTML = `Opponent chose Paper. <br>You <span class="green">WIN</span>!!`;
        var critSound = new Audio('https://fleury14.github.io/cyo/sound/persona5-crit.wav');
        critSound.play();
        break;
      case 3:
        document.querySelector('#who-won').innerHTML = `Opponent chose Scissors. <br>It's a -<span class="blue">DRAW</span>-`;
        var audioDraw = new Audio('mp3/draw.wav');
	      audioDraw.play();
        break;
    }
  }
}

