document.querySelector('.a-1').addEventListener('click', function() {
  thinking.style.visibility = 'visible';
  setTimeout (function() {
    firstDot.style.visibility = 'visible';
  }, 500);
  setTimeout (function() {
    secondDot.style.visibility = 'visible';
  }, 1000);
  setTimeout (function() {
    thirdDot.style.visibility = 'visible';
  }, 1500);
  setTimeout (function() {
    thinking.style.visibility = 'hidden';
    firstDot.style.visibility = 'hidden';
    secondDot.style.visibility = 'hidden';
    thirdDot.style.visibility = 'hidden';
  }, 2000);
  var randomNum = Math.ceil(Math.random()*3);
  if (xOrO == 'x') {
    switch(randomNum) {
      case 1:
        setTimeout (function() {
          thinking.style.visibility = 'hidden';
        }, 2000);
        setTimeout (function() {
          a2o.style.display = 'block';
        }, 2000);
        break;
      case 2:
      setTimeout (function() {
        b1o.style.display = 'block';
      }, 2000);
        break;
      case 3:
      setTimeout (function() {
        b2o.style.display = 'block';
      }, 2000);
    }
  } else if (xOrO == 'o') {
    a2o.style.display = 'block';
  }
});
